import React, { useState } from 'react';
import './style.css';

const WingsCafeInventorySystem = () => {
    const [products, setProducts] = useState([]);
    const [users, setUsers] = useState([]);
    const [productName, setProductName] = useState('');
    const [description, setDescription] = useState('');
    const [category, setCategory] = useState('');
    const [price, setPrice] = useState(0);
    const [quantity, setQuantity] = useState(0);
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    // Function to add a new product
    const addProduct = (e) => {
        e.preventDefault();
        const newProduct = { productName, description, category, price, quantity };
        setProducts([...products, newProduct]);
        setProductName('');
        setDescription('');
        setCategory('');
        setPrice(0);
        setQuantity(0);
    };

    // Function to add a new user
    const addUser = (e) => {
        e.preventDefault();
        const newUser = { username };
        setUsers([...users, newUser]);
        setUsername('');
        setPassword('');
    };

    // Function to handle stock increment
    const addStock = (index) => {
        const updatedProducts = products.map((product, i) => 
            i === index ? { ...product, quantity: product.quantity + 1 } : product
        );
        setProducts(updatedProducts);
    };

    // Function to handle stock decrement
    const reduceStock = (index) => {
        const updatedProducts = products.map((product, i) => 
            i === index && product.quantity > 0 ? { ...product, quantity: product.quantity - 1 } : product
        );
        setProducts(updatedProducts);
    };

    return (
        <div className="container">
            <h1>Wings Cafe Inventory System</h1>

            <div className="dashboard">
                <h2>Current Stock Levels</h2>
                <table id="inventoryTable">
                    <thead>
                        <tr>
                            <th>Product Name</th>
                            <th>Description</th>
                            <th>Price</th>
                            <th>Quantity</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {products.map((product, index) => (
                            <tr key={index}>
                                <td>{product.productName}</td>
                                <td>{product.description}</td>
                                <td>{product.price}</td>
                                <td>{product.quantity}</td>
                                <td>
                                    <button className="action-button add-stock" onClick={() => addStock(index)}>Add Stock</button>
                                    <button className="action-button reduce-stock" onClick={() => reduceStock(index)}>Reduce Stock</button>
                                    <button className="action-button delete">Delete</button>
                                    <button className="action-button update">Update</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>

            <div className="product-form">
                <h2>Product Management</h2> 
                <form onSubmit={addProduct}>
                    <label>Product Name:</label>
                    <input type="text" value={productName} onChange={(e) => setProductName(e.target.value)} required />
                    
                    <label>Description:</label>
                    <input type="text" value={description} onChange={(e) => setDescription(e.target.value)} required />
                    
                    <label>Category:</label>
                    <input type="text" value={category} onChange={(e) => setCategory(e.target.value)} required />
                    
                    <label>Price:</label>
                    <input type="number" value={price} onChange={(e) => setPrice(Number(e.target.value))} required />
                    
                    <label>Initial Quantity:</label>
                    <input type="number" value={quantity} onChange={(e) => setQuantity(Number(e.target.value))} required />

                    <button type="submit" className="add-product-button">Add Product</button>
                </form>
            </div>

            {/* User Management */}
            <div className="user-management">
                <h2>User Management</h2>
                <form onSubmit={addUser}>
                    <label>Username:</label>
                    <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} required />
                    
                    <label>Password:</label>
                    <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} required />
                    
                    <button type="submit" className="add-user-button">Add User</button>
                </form>

                <table id="userTable">
                    <thead>
                        <tr>
                            <th>Username</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {users.map((user, index) => (
                            <tr key={index}>
                                <td>{user.username}</td>
                                <td><button className="action-button delete">Delete</button></td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
};

export default WingsCafeInventorySystem;
